function get_icono( $object = null ) {
  if ($object == null) return false;

  $icono = get_field('icono_svg', $object);
  if( $icono ) return '<div class="icono">' . $icono . '</div>';

  $icono_id = get_field('icono_img', $object);
  if ($icono_id) {
    $icono = wp_get_attachment_image( $icono_id, 'medium', false, array( 'class' => 'icono' ) );
    return $icono;
  }

  return false;

}


